import React, { useCallback, useEffect, useState } from "react";
import "./layout.css";
import CourseCard from "../courses/CourseCard";
import IconSection from "../UI/IconSection";
import axios from "axios";
import STATIC_VALUES from "../../STATIC_VALUES";

const Layout = () => {
  const [courses, setCourses] = useState([]);
  const [sorting, setSorting] = useState(null);
  const [hiddens, setHidden] = useState(true);
  const [bookmarked, setBookmarked] = useState([]);
  const [reported, setReported] = useState([]);
  const [refetch, setRefetch] = useState();

  useEffect(() => {
    async function fetchData() {
      let url = `${STATIC_VALUES.SERVER_URL}/api/courses`;
      if (sorting) {
        url = `${url}?sortby=${sorting.sortBy.toLowerCase()}&sortorder=${sorting.sortOrder.toLowerCase()}`;
      }
      if (hiddens) {
        if (sorting) {
          url = `${url}&hidden=false`;
        } else url = `${url}?hidden=false`;
      }
      const res = await axios.get(url);
      setCourses(res.data);
    }
    fetchData();
  }, [sorting, hiddens, reported, bookmarked, refetch]);

  const setAction = useCallback(
    (action, id) => {
      const doAction = async (patchData, id) => {
        let url = `${STATIC_VALUES.SERVER_URL}/api/courses/${id}`;
        const data = await axios.patch(url, patchData);
        setRefetch(data);
      };
      switch (action) {
        case "hide":
          doAction({ isHidden: true }, id);
          break;
        case "unhide":
          doAction({ isBookmarked: false }, id);
          break;
        case "bookmark":
          setBookmarked(Array.from(new Set([...bookmarked, id])));
          doAction({ isBookmarked: true }, id);
          break;
        case "unbookmark":
          setBookmarked(bookmarked.filter((item) => item !== id));
          doAction({ isBookmarked: false }, id);
          break;
        case "report":
          setReported(Array.from(new Set([...reported, id])));
          doAction({ isReported: true }, id);
          break;
        case "unreport":
          setReported(reported.filter((item) => item !== id));
          doAction({ isReported: false }, id);
          break;
        default:
          break;
      }
      console.log(hiddens, reported, bookmarked);
    },
    [bookmarked, hiddens, reported]
  );

  return (
    <div className="layout">
      <IconSection setSorting={setSorting} />
      {courses.map((item, index) => {
        const backgroundColor = index % 2 === 0 ? "#FFFCF2" : "#fff";
        return (
          <div className="card" key={index} style={{ backgroundColor }}>
            <CourseCard item={item} setAction={setAction} />
          </div>
        );
      })}
    </div>
  );
};

export default Layout;
