import React, { useEffect, useState } from "react";
import "./layout.css";
import Navbar from "../Navbar/Navbar";
import CourseCard from "../courses/CourseCard";
import IconSection from "../UI/IconSection";
import axios from "axios";
import STATIC_VALUES from "../../STATIC_VALUES";
const Layout = () => {
  const [courses, setCourses] = useState([]);
  useEffect(() => {
    async function fetchData() {
      const res = await axios.get(`${STATIC_VALUES.SERVER_URL}/api/courses`);
      setCourses(res.data);
    }
    fetchData();
  }, []);
  const coursedetail = [
    {
      key: 1,
      name: "Vastu Course",
      enrollment: "34",
      price: "990",
      duration: "6",
      contactNumber: "+91 7767697979",
      description:
        "Vastu is an old Indian investigation of architecture and structures that help make an appropriate setting",
    },

    {
      key: 2,
      name: "Astrology  Course",
      enrollment: "44",
      price: "940",
      duration: "8",
      contactNumber: "+91 7767697979",
      description:
        "Vastu is an old Indian investigation of architecture and structures that help make an appropriate setting",
    },
    {
      key: 3,
      name: "Yoga Course",
      enrollment: "54",
      price: "890",
      duration: "12",
      contactNumber: "+91 7767697979",
      description:
        "Vastu is an old Indian investigation of architecture and structures that help make an appropriate setting",
    },
    {
      key: 4,
      name: "Yoga Course",
      enrollment: "54",
      price: "890",
      contactNumber: "+91 - 7767697979",
      description:
        "Vastu is an old Indian investigation of architecture and structures that help make an appropriate setting",
    },
  ];
  return (
    <div className="layout">
      <Navbar />

      <IconSection />

      {coursedetail.map((item, index) => {
        const backgroundColor = index % 2 === 0 ? "#FFFCF2" : "#fff";
        return (
          <div className="card" key={index} style={{ backgroundColor }}>
            <CourseCard item={item} />
          </div>
        );
      })}
    </div>
  );
};

export default Layout;
